import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function Post() {
  let { id } = useParams();
  const [postObject, setPostObject] = useState({});
  const [commentList, setCommentList] = useState([]);
  const [newComment, setNewComment] = useState("");

  const addComment = () => {
    axios.post("http://localhost:3001/comments", {
      commentBody : newComment,
      PostID : id,
    }).then((response) => {
      console.log("Comment added")
    })
  }

  useEffect(() => {
    axios.get(`http://localhost:3001/posts/byID/${id}`).then((response) => {
      setPostObject(response.data);
    });

    axios.get(`http://localhost:3001/comments/byID/${id}`).then((response) => {
      setCommentList(response.data);
    });
  }, []);

  return (
    <div className="postPage">
      <div className="leftSide">
        <div className="post" id="individual">
          <div className="title"> {postObject.title} </div>
          <div className="body">{postObject.postText}</div>
          <div className="footer">{postObject.username}</div>
        </div>
      </div>
      <div className="rightSide">
        <div className = "addCommentContainer">
          <input type="text" placeholder="Comment likhdo" autoComplete="off" onChange={(event) => {setNewComment(event.target.value)}}/>
            <button onClick={addComment}>Add comment</button>
        </div>
        <div className = "listOfComments">
          {commentList.map((comment, key)=>{
            return <div key={key} className="comment">{comment.commentBody}</div>
          })}
        </div>
      </div>
    </div>
  );
}

export default Post;
